jQuery("document").ready(function($){

    var nav = $('.menu');

    $(window).scroll(function () {
        if ($(this).scrollTop() > 95) {
            nav.addClass("f-nav");

        } else {
            nav.removeClass("f-nav");
        }
    });

    var social=$('.social');
    $(window).resize(function(){
        if  ($(this).width()<1000){
            social.hide();
        }else{
            social.show();
        }
    });

});

jQuery("document").ready(function($){
    $('nav ul li').on('click', function(){
        $('li.current').removeClass('current');
        $(this).addClass('current');
    });
});

$(document).ready(function(){

  $(document).on('init.slides', function() {
    $('.loading-container').fadeOut(function() {
      $(this).remove();
    });
  });


  $('.fancybox').fancybox({
      beforeShow : function(){
          $(".fancybox-skin").addClass("form_reset");
      },
      'closeBtn' : false
  });



    $('#first_form').validate();
    $('#price_form').validate();
  $('#second_form').validate();
  $('#consultation_form').validate();
    $('#calendar_form').validate();
  $('.slider-block form').ajaxForm({
      success: function () {
          $(".tnx-link").trigger('click');
      },
      data: {
          title: ' Бесплатная консультация '
      }
  });
  $('"first_form"').ajaxForm({
      success: function () {
          $(".tnx-link").trigger('click');
      },
      data: {
          title: ' Закажи фотосессию '
      }
  });
  $('#second_form').ajaxForm({
      success: function () {
          $(".tnx-link").trigger('click');
      },
      data: {
          title: ' Закажи фотосессию '
      }
  });
    $('.calendar-form form').ajaxForm({
        success: function () {
            $(".tnx-link").trigger('click');
        },
        data: {
            title: ' Забронировать фотосудию '
        }
    });
  $('.price-big-block form').ajaxForm({
      success: function () {
          $(".tnx-link").trigger('click');
      },
      data: {
          title: ' Получить сертификат '
      }
  });

  $('input[name="phone"]').mask("+7 (999) 999-99-99");
  $('input[name="date"]').mask("99.99.2099");




  $('.reviews .show-all').click(function(){
      $(this).prev('.other-reviews').slideToggle('0', function(){
          if ($(this).is(":hidden")) $(".reviews .show-all").html("Посмотреть еще");
          else $(".reviews .show-all").html("Скрыть");
      });
  })

});

